Select COB_DATE,CCC_PL_REPORTING_REGION, SUM(VEGA_GREEK) as VEGA_GREEK, SUM(THETA_GREEK) as THETA_GREEK,SUM(GAMMA_GREEK) as GAMMA_GREEK, PRODUCT_TYPE_CODE, TIME_BUCKET_CALENDAR, QUARTERS FROM( SELECT COB_DATE, PRODUCT_SUB_TYPE_CODE,CCC_PL_REPORTING_REGION, ccc_product_line, PRODUCT_TYPE_CODE, time_bucket_calendar, case when EXPIRATION_DATE < ('2019-09-01') then time_bucket_quarter end as quarters, sum(USD_CM_GAMMA)/20 as GAMMA_GREEK, sum(USD_CM_THETA) as THETA_GREEK,sum(RAW_CM_KAPPA) as VEGA_GREEK FROM cdwuser.U_CM_MSR Where COB_DATE IN ('02-28-2018','01-31-2018') AND ((CCC_DIVISION='COMMODITIES' AND CCC_BUSINESS_AREA= 'AP EU ELECTRICNATURAL GAS') /*OLD LOGIC*/ OR (CCC_DIVISION='FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA='COMMODITIES' AND CCC_STRATEGY = 'AP EU POWER & GAS')) /*NEW LOGIC*/ and CCC_TRD_BOOK <> 'DEVELOPMENT' Group BY COB_DATE,PRODUCT_SUB_TYPE_CODE, PRODUCT_TYPE_CODE,EXPIRATION_DATE,time_bucket_quarter,CCC_PL_REPORTING_REGION, ccc_product_line, time_bucket_calendar) abc GROUP BY COB_DATE, PRODUCT_TYPE_CODE, TIME_BUCKET_CALENDAR,CCC_PL_REPORTING_REGION, QUARTERS FOR READ ONLY