SELECT case when CCC_BUSINESS_AREA = 'COMMODITIES' then CCC_PRODUCT_LINE else CCC_BUSINESS_AREA end as CCC_BUSINESS_AREA,CCC_PL_REPORTING_REGION, COB_DATE, IR_CCY, CASE WHEN PRODUCT_SUB_TYPE_CODE IN ('N EAST CONSUMPTI', 'N EAST CONSUMPTION') THEN 'N EAST CONSUMPTI' WHEN PRODUCT_SUB_TYPE_CODE IN ('N EAST APPALACHI', 'N EAST APPALACHIA') THEN 'N EAST APPALACHI' WHEN PRODUCT_SUB_TYPE_CODE IN ('POLYPROPELENE HO', 'POLYPROPELENE HOMOPOLYMER') THEN 'POLYPROPELENE HO' WHEN PRODUCT_SUB_TYPE_CODE IN ('POLYPROPYLENE CO', 'POLYPROPYLENE COPOLYMER') THEN 'POLYPROPYLENE CO' WHEN PRODUCT_SUB_TYPE_CODE IN ('PURE TEREPHTHALI', 'PURE TEREPHTHALIC ACID') THEN 'PURE TEREPHTHALI' WHEN PRODUCT_SUB_TYPE_CODE IN ('GSCI-PRECIOUS ME', 'GSCI-PRECIOUS METALS') THEN 'GSCI-PRECIOUS ME' WHEN PRODUCT_SUB_TYPE_CODE IN ('ZERO PRICED EXPO', 'ZERO PRICED EXPOSURE') THEN 'ZERO PRICED EXPO' WHEN PRODUCT_SUB_TYPE_CODE IN ('DJUBS-BASE METAL', 'DJUBS-BASE METALS') THEN 'DJUBS-BASE METAL' WHEN PRODUCT_SUB_TYPE_CODE IN ('GAS SULFUR CREDI', 'GAS SULFUR CREDIT') THEN 'GAS SULFUR CREDI' WHEN PRODUCT_SUB_TYPE_CODE IN ('GASOIL TIMESPREA', 'GASOIL TIMESPREAD') THEN 'GASOIL TIMESPREA' WHEN PRODUCT_SUB_TYPE_CODE IN ('USD INTEREST RAT', 'USD INTEREST RATE') THEN 'USD INTEREST RAT' ELSE PRODUCT_SUB_TYPE_CODE END AS PRODUCT_SUB_TYPE_CODE, PRODUCT_TYPE_CODE, QUARTERS, TIME_BUCKET_CALENDAR, SUM(DOLLAR_GREEK) AS DOLLAR_GREEK From (select prod_pos_name_description, CCC_PRODUCT_LINE,product_sub_type_code, product_type_code, CMDTY_CD,EXPIRATION_DATE,time_bucket_quarter,CCC_TRD_BOOK, product_sub_type_name,COB_DATE,CCC_BUSINESS_AREA,time_bucket_annual,TIME_BUCKET_CALENDAR,CCC_STRATEGY,CCC_PL_REPORTING_REGION, sum(RAW_CM_Delta) as RAW_GREEK,Sum (USD_CM_Delta)as DOLLAR_GREEK, case when product_type_code='COAL' then 'COAL' when prod_pos_name_description in ('PHYSICAL', 'SWAPTION') then 'phys' when prod_pos_name_description like 'EUA%' and product_type_code='CO2' then 'eua' when product_type_code='CO2' then prod_pos_name_description when product_sub_type_name like 'SPREAD EUROPEAN' then 'Gas Capacity Option' when product_sub_type_name like 'ELECTRICITY TRANSMISSION%' then 'Electricity Transmission Option' end as PHYS, case when product_type_code in ('CURRENCY','INTEREST RATE') then 'IR_Ccy' else 'x_IR_Ccy' end as IR_Ccy, case when EXPIRATION_DATE < ('2019-09-01') then time_bucket_quarter end as quarters, case when CCC_BUSINESS_AREA = 'METALS' and product_type_code in ('BASEMETAL','PRECIOUSMETAL', 'FUND') then 'METAL' when product_type_code in ('GRAINS', 'COFFEE/COCOA', 'SUGAR', 'VEG OILS', 'LIVESTOCK', 'SOFT', 'COTTON', 'SOFT / LIVESTOCK') then 'Soft' when product_type_code in ('EAST OFF', 'EAST PEAK','MIDWEST OFF', 'MIDWEST PEAK','TEXAS OFF', 'TEXAS PEAK', 'WEST OFF', 'WEST PEAK','EURO PWR','EAST INTERCONNECT OF','EAST INTERCONNECT PE','TEXAS INTERCONNECT O', 'TEXAS INTERCONNECT P', 'ERCOT', 'WEST INTERCONNECT OF', 'WEST INTERCONNECT PE') then 'ELECTRICITY' when product_type_code in ('NATGAS','EUR NG') then 'NATGAS' when product_type_code in ('CRUDE','DIST','FUEL','GAS','JET','NAPHTHA', 'ETHANOL', 'NGL','CLEAN FREIGHT','DIRTY FREIGHT') or CMDTY_CD='SPREADS' then 'OIL LIQUIDS' when product_type_code in('DRY FREIGHT','COAL') then product_type_code end as product_type FROM cdwuser.U_CM_MSR Where COB_DATE IN ('2018-02-28','2018-02-21') AND ((CCC_DIVISION='COMMODITIES' AND CCC_BUSINESS_AREA not in ('CREDIT','MS CVA MNE - COMMOD', 'COMMODS FINANCING','INVESTMENTS AND STR TRANS','INVESTOR BUSINESS')) OR(CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' AND CCC_PRODUCT_LINE NOT IN ('COMMOD LENDING', 'COMMOD EXOTICS', 'COMMOD INDEX') AND CCC_STRATEGY NOT IN ('MS CVA MNE - COMMOD', 'CMD LEGACY LAONS & CLAIMS', 'CVA RISK MANAGEMENT', 'FVA RISK MANAGEMENT', 'FUNDING COLLATERAL COMM'))) /* NEW LOGIC*/ and CCC_TRD_BOOK <> 'DEVELOPMENT' and product_type_code not in ('TBD','MISC') AND (CCC_PL_REPORTING_REGION IN ('EUROPE','EMEA')) group by prod_pos_name_description, CCC_PRODUCT_LINE,product_sub_type_code,product_type_code,product_sub_type_name,COB_DATE,CCC_PL_REPORTING_REGION, CCC_BUSINESS_AREA,TIME_BUCKET_CALENDAR,TIME_BUCKET_ANNUAL, CMDTY_CD, EXPIRATION_DATE,time_bucket_quarter,CCC_TRD_BOOK,CCC_STRATEGY) abc GROUP by CCC_BUSINESS_AREA, CCC_PRODUCT_LINE, COB_DATE, IR_CCY, PRODUCT_SUB_TYPE_CODE,CCC_PL_REPORTING_REGION, PRODUCT_TYPE_CODE, QUARTERS, TIME_BUCKET_CALENDAR FOR READ ONLY