Select CASE WHEN (A.CCC_BUSINESS_AREA IN ('NA ELECTRICITYNATURAL GAS') OR A.CCC_PRODUCT_LINE IN ('NA POWER & GAS')) THEN 'NA ELECTRICITYNATURAL GAS' WHEN (A.CCC_BUSINESS_AREA IN ('INVESTOR BUSINESS') OR A.CCC_PRODUCT_LINE IN ('COMMOD EXOTICS', 'COMMOD INDEX')) THEN 'INVESTOR BUSINESS' WHEN (A.CCC_BUSINESS_AREA IN ('METALS') OR A.CCC_PRODUCT_LINE IN ('PRECIOUS METALS') OR A.CCC_STRATEGY IN ('BULKS', 'BASE METALS')) THEN 'PRECIOUS METALS' WHEN ((A.CCC_BUSINESS_AREA in ('OIL LIQUIDS') AND A.CCC_PRODUCT_LINE NOT IN ('LEGACY OIL')) OR A.CCC_PRODUCT_LINE IN ('OIL & PRODUCTS')) THEN 'OIL LIQUIDS' WHEN (A.CCC_BUSINESS_AREA IN ('OLYMPUS') OR A.CCC_STRATEGY IN ('OLYMPUS')) THEN 'OLYMPUS' else a.CCC_BUSINESS_AREA end as CCC_BUSINESS_AREA, a.CCC_PL_REPORTING_REGION, a.COB_DATE, a.CCC_TAPS_COMPANY, a.PRODUCT_TYPE_CODE, a.CCC_DIVISION, a.TIME_BUCKET_CALENDAR, SUM(a.DOLLAR_GREEK) as DOLLAR_GREEK, SUM(a.RAW_GREEK) as RAw_GREEK From ( Select prod_pos_name_description, COB_DATE,ccc_business_area, CCC_TAPS_COMPANY,CCC_PL_REPORTING_REGION, CCC_PRODUCT_LINE, CCC_STRATEGY, CCC_DIVISION,product_type_code,time_bucket_calendar, product_sub_type_code, TIME_BUCKET_ANNUAL,EXPIRATION_DATE,time_bucket_quarter, sum(cast(USD_CM_Delta as numeric(15,5))) as dollar_greek, SUM (CASE WHEN PRODUCT_TYPE_CODE = 'TIMESPREAD' AND PROD_POS_NAME_DESCRIPTION = 'WCS POST SPREAD' THEN 0 WHEN (book = 'COMXO' and CCC_PRODUCT_LINE IN ('COMMOD EXOTICS')) then CAST(USD_CM_KAPPA_ABSOLUTE AS NUMERIC (15, 5)) ELSE CAST (RAW_CM_KAPPA AS NUMERIC (15, 5)) END) as raw_greek, case when product_sub_type_code ='ALUMINUM-20MT' then 'Aluminum' when product_type_code in ('BASEMETAL', 'PRECIOUSMETAL') then product_sub_type_code end as Metal FROM cdwuser.U_CM_MSR Where COB_DATE IN ('2018-02-28','2018-02-21') AND NOT (CCC_DIVISION IN ('COMMODITIES')) /*OLD LOGIC*/ AND NOT (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES') /*NEW LOGIC*/ AND CCC_TAPS_COMPANY in ('0302','0342','0517') AND (CCC_PL_REPORTING_REGION IN ('EUROPE','EMEA')) Group By prod_pos_name_description, COB_DATE,ccc_business_area, CCC_TAPS_COMPANY,CCC_PL_REPORTING_REGION, CCC_PRODUCT_LINE, CCC_STRATEGY, CCC_DIVISION, product_type_code,time_bucket_calendar,TIME_BUCKET_ANNUAL, EXPIRATION_DATE,time_bucket_quarter, product_sub_type_code)a GROUP BY a.CCC_BUSINESS_AREA, a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_TAPS_COMPANY, a.PRODUCT_TYPE_CODE, a.CCC_DIVISION, a.TIME_BUCKET_CALENDAR, CCC_PRODUCT_LINE, CCC_STRATEGY