SELECT a.COB_Date         ,a.CCC_BUSINESS_AREA         ,MNE_FLAG         ,SUM(a.USD_PV10_BENCH_COMP) AS PV10 FROM cdwuser.U_DM_CVA a WHERE  (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and CCC_PL_REPORTING_REGION in ('EMEA') AND          a.CCC_BUSINESS_AREA IN (                 'MS CVA MNE - FID'                 ,'MS CVA MNE - COMMOD'                 )         AND a.ccc_product_line NOT IN (                 'CREDIT LOAN PORTFOLIO'                 ,'CMD STRUCTURED FINANCE'                 )         AND a.USD_PV10_BENCH_COMP IS NOT NULL         AND a.PRODUCT_SUB_TYPE_CODE NOT IN (                 'MNE'                 ,'MNE_FVA_NET'                 ) AND A.CURVE_NAME <> 'ms_seccpm' GROUP BY a.COB_Date         ,a.CCC_BUSINESS_AREA         ,MNE_FLAG