SELECT      a.COB_DATE,      a.ISSUER_COUNTRY_CODE_DECOMP,      SUM (a.USD_EQ_DELTA_DECOMP) AS USD_EQ_DELTA FROM cdwuser.U_DECOMP_MSR a      WHERE      1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_PL_REPORTING_REGION in ('JAPAN','ASIA PACIFIC') AND A.CCC_TAPS_COMPANY in ('0302','0347','0853','4043','4298','4863','6120','6899','6837','6893','4044','5869','0856','6325','0301','0893','0993') AND       a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND     /*a.CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND*/      a.CCC_BANKING_TRADING = 'TRADING' AND      a.USD_EQ_DELTA_DECOMP IS NOT NULL GROUP BY      a.COB_DATE,      a.ISSUER_COUNTRY_CODE_DECOMP