SELECT     COB_DATE,     product_type_code,     product_sub_type_code,     CCC_BUSINESS_AREA,     CCC_PL_REPORTING_REGION,     SUM (USD_CM_Delta) AS USD_CM_Delta FROM cdwuser.U_CM_MSR A WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND        CCC_BANKING_TRADING='TRADING' AND     product_type_code IN ('GRAINS', 'VEG OILS', 'SOFT / LIVESTOCK') AND /*    (a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDFUTOPT') OR     (a.VAR_EXCL_FL <> 'Y' AND a.MEASURE_VAR_EXCL_FL <> 'Y')) AND*/     CCC_DIVISION = 'FIXED INCOME DIVISION' AND     CCC_BUSINESS_AREA = 'COMMODITIES' /*AND     CCC_TRD_BOOK <> 'DEVELOPMENT'*/ GROUP BY     COB_DATE,     product_type_code,     product_sub_type_code,     CCC_PL_REPORTING_REGION,     CCC_BUSINESS_AREA