SELECT     x.*,     CASE WHEN CURRENCY IN ('USD', 'EUR', 'GBP', 'AUD', 'CAD', 'CHF', 'NZD', 'SEK', 'NOK', 'DKK','JPY') THEN 'G10'     ELSE 'Non-G10' END AS ccy_type FROM     (         SELECT             a.COB_DATE,           a.CCC_BUSINESS_AREA,a.CCC_STRATEGY,a.CCC_PRODUCT_LINE,           CASE WHEN FEED_SOURCE_NAME = 'CORISK' THEN substr (PROD_POS_NAME_DESCRIPTION,1,                                                              3) WHEN A.CURVE_NAME = 'ub1nw_disc' THEN 'UB1'             ELSE a.CURRENCY_OF_MEASURE END AS CURRENCY,             CASE WHEN (a.BOOK = 'COLLA' AND                        a.VERTICAL_SYSTEM LIKE 'PERSIST%') THEN 'CTDVA' WHEN (a.BOOK = 'COLVA' AND                                                                              a.VERTICAL_SYSTEM LIKE 'PERSIST%') THEN 'LVA' WHEN                  PRODUCT_SUB_TYPE_CODE IN ('MPE_FVA_RAW', 'MPE_FVA') THEN 'MPE FVA' WHEN PRODUCT_SUB_TYPE_CODE IN ('MNE_FVA',                                                                                                                        'MNE_FVA_NET')                  THEN 'MNE FVA' WHEN (PRODUCT_SUB_TYPE_CODE IN ('MPE_CVA', 'MPE', 'MPE_PROXY', 'MNE_CP') OR                                      (BOOK IN ('CV2LP', 'CV2LN', 'CVPL1', 'CV2LD', 'CVPL2', 'FVPL2') AND                                       BU_RISK_SYSTEM LIKE 'STS%') OR                                      BOOK = '1679') THEN 'MPE CVA' WHEN PRODUCT_SUB_TYPE_CODE IN ('MNE_CVA', 'MNE') THEN 'MNE CVA'             ELSE 'Hedge' END AS TYPE_FLAG,             SUM (a.USD_IR_UNIFIED_PV01) AS USD_IR_UNIFIED_PV01         FROM cdwuser.U_IR_MSR a         WHERE (a.cob_date = '2018-02-28') AND CCC_TAPS_COMPANY in ('0302') AND              (a.CCC_BUSINESS_AREA IN ('CPM TRADING (MPE)','CPM', 'CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') OR              a.CCC_STRATEGY IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES','EQ XVA HEDGING'))         GROUP BY             a.COB_DATE,           a.CCC_BUSINESS_AREA,a.CCC_STRATEGY,a.CCC_PRODUCT_LINE,           CURRENCY, TYPE_FLAG     )     x