SELECT     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     a.CCC_BANKING_TRADING,     SUM (CAST(a.USD_IR_KAPPA AS DOUBLE PRECISION)) / 10 AS USD_IR_KAPPA FROM cdwuser.U_IR_MSR_INTRPLT a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_TAPS_COMPANY in ('4391', '4341') AND      (a.VAR_EXCL_FL <> 'Y') AND     (a.CCC_DIVISION IN ('FIXED INCOME DIVISION') OR     a.CCC_DIVISION IN ('BANK RESOURCE MANAGEMENT') OR     (a.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION') AND a.CCC_BUSINESS_AREA NOT IN ('INTERNATIONAL WEALTH MGMT'))     OR a.CCC_DIVISION IN ('INSTITUTIONAL SECURITIES OTHER')) AND     a.USD_IR_KAPPA IS NOT NULL GROUP BY     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     a.CCC_BANKING_TRADING